mapper.update:
	UPDATE STOCK_ID_CARD C
   SET DEPOSIT_PWD = #{depositPwd},TELEPHONE = #{telephone,jdbcType=VARCHAR}
 WHERE EXISTS (SELECT 1
          FROM STOCK_USER U
         WHERE C.USER_ID = U.ID
           AND U.NICKNAME = #{nickname}
          )

mapper.select:
SELECT ASSIGN_RADIO, STOP_RADIO, WARN_RADIO,FEE_RADIO
  FROM STOCK_FUND_ASSIGN_RADIO
 WHERE NICKNAME = #{nickname}

Select count(1) From Stock_User_Withdrawl_Cash s, Stock_Id_Card Card Where Exists (Select 1 From Stock_User u Where u.Id = Card.User_Id And s.Nickname = u.Nickname)
and nickname like '%'||#{nickname}||'%'
and status = #{depositType}

mapper.update:
UPDATE STOCK_USER_WALLET W
   SET W.BALANCE = W.BALANCE + to_number(#{userCash})
 WHERE W.NICKNAME = #{nickname}

mapper.select:
Select Assign_Referee_Red_Packet
  From Stock_Fund_Assign_Radio r
 Where r.Nickname = #{referee}

Select Red_Packet
  From Stock_Referee_Red_Packet p
 Where p.REFEREE_NICKNAME = #{referee}

Select Assign_Register_Red_Packet
  From Stock_Fund_Assign_Radio r
 Where r.Nickname = #{nickname}

mapper.update:
UPDATE STOCK_USER_WALLET W
   SET W.BALANCE = W.BALANCE + to_number(#{redPacket})
 where w.nickname = #{nickname}

	UPDATE STOCK_USER_WALLET W
   	   SET W.BALANCE = W.BALANCE + #{redPacket}
	 where w.nickname = #{nickname}

mapper.select:
Select 1
  From Stock_User_Wallet w
 Where w.Nickname = #{nickname}
   And w.Balance > #{fund}

SELECT COUNT(1) FROM STOCK_USER_OPERATE_MAININFO M WHERE 1=1 AND M.ACTUAL_CASH <= M.STOP_CASH
AND M.is_current='1'
AND M.NICKNAME = #{nickname}

SELECT M.ID, M.NICKNAME, M.IS_CURRENT STATUS, M.STOP_CASH STOP_LINE, M.WARN_CASH WARN_LINE, M.ACTUAL_CASH, (Select Id From Stock_User u Where u.Nickname = m.Nickname) Userid, (SELECT T.TRADE_ACOUNT FROM STOCK_USER_OPERATE_ACOUNT T WHERE T.NICKNAME = M.NICKNAME AND T.IS_CURRENT = '1') TRADE_ACOUNT FROM STOCK_USER_OPERATE_MAININFO M WHERE 1=1 AND M.ACTUAL_CASH <= M.STOP_CASH
AND M.is_current='1'
AND M.NICKNAME = #{nickname}

select T.ID, T.NICKNAME, (CASE WHEN T.USER_RANGE = '1' THEN '小生' WHEN T.USER_RANGE = '2' THEN '秀才' WHEN T.USER_RANGE = '3' THEN '探花' WHEN T.USER_RANGE = '5' THEN '榜眼' ELSE '状元' END) USER_RANGE, T.STOP_RADIO, T.WARN_RADIO, T.ASSIGN_RADIO, T.ASSIGN_REFEREE_RED_PACKET, T.ASSIGN_REGISTER_RED_PACKET, T.FEE_RADIO, T.ASSIGN_CASH, T.UP_LINE_PERCENT, T.DOWN_LINE_PERCENT FROM STOCK_FUND_ASSIGN_RADIO T
where T.NICKNAME = #{nickname}
ORDER BY ID DESC

SELECT T.NICKNAME, T.BALANCE, (SELECT U.TELEPHONE FROM STOCK_USER U WHERE U.NICKNAME = T.NICKNAME) PHONE, (SELECT O.TRADE_ACOUNT FROM STOCK_USER_OPERATE_ACOUNT O WHERE O.NICKNAME = T.NICKNAME AND O.IS_CURRENT = '1') TRADE_ACOUNT FROM STOCK_USER_WALLET T WHERE T.BALANCE < 0
and T.NICKNAME = #{nickname}
ORDER BY ID DESC
