mapper.select:
SELECT T.PRODUCT_CODE, T.FINANCE_PRODUCT, T.FINANCE_TOTAL_LIMIT, T.ANNUAL_INCOME, T.EXPIRE_DAY, T.MAX_LIMIT, T.MIN_LIMIT, TO_CHAR(T.CARRY_DATE, 'yyyy-mm-dd') CARRYDATE, TO_CHAR(T.CARRY_DATE + T.EXPIRE_DAY, 'yyyy-mm-dd') SETTLE_DATE, (SELECT COUNT(1) FROM STOCK_FINANCE_RECORD R WHERE R.PRODUCT_CODE = T.PRODUCT_CODE GROUP BY R.PRODUCT_CODE) NUM, (T.CARRY_DATE + T.EXPIRE_DAY -To_Date(To_Char(Sysdate, 'yyyy-mm-dd'), 'yyyy-mm-dd')) leaveDays FROM STOCK_FINANCE_PRODUCT T
where to_char(PUBLISH_DATE,'yyyy-mm-dd') = #{date}
ORDER BY PUBLISH_DATE DESC

mapper.update:
        UPDATE STOCK_USER_WALLET W set
       		   W.BALANCE = (W.BALANCE + #{income})
	 	WHERE  W.NICKNAME = #{nickname}

        UPDATE STOCK_USER_WALLET W set
       		   W.BALANCE = (W.BALANCE + #{fund})
	 	WHERE  W.NICKNAME = #{nickname}

mapper.select:
    select count(1)
	 from STOCK_FINANCE_RECORD r 
	where product_code = #{productCode}
