mapper.update:
UPDATE STOCK_USER_WALLET W
   SET W.BALANCE = W.BALANCE - to_number(#{guaranteeCash}) 
 WHERE W.NICKNAME = #{nickname}

	        UPDATE STOCK_USER_WALLET W set
        		   W.BALANCE = (W.BALANCE - #{fee,jdbcType=FLOAT})
		 	WHERE  W.NICKNAME = #{nickname,jdbcType=VARCHAR}

	        UPDATE STOCK_USER_WALLET W set
        		   W.BALANCE = (W.BALANCE - #{fee,jdbcType=FLOAT})
		 	WHERE  W.NICKNAME = #{nickname,jdbcType=VARCHAR}

Update Stock_User_Wallet w
   Set w.Balance = w.Balance + to_number(#{fund})
 Where w.Nickname = #{nickname}

mapper.select:
select (select -sum(t.Fund) From Stock_Fund_Flow t Where To_Char(t.Transaction_Time, 'yyyy-mm-dd') = a.transaction_day And Trim(t.Transaction_Type) = '30') as serviceCharge , ( Select Sum(Fund) From Stock_Fund_Flow t Where To_Char(t.Transaction_Time, 'yyyy-mm-dd') = a.transaction_day And Trim(t.Transaction_Type) = '120' ) as commission ,( Select Sum(Fund) From Stock_Fund_Flow t Where To_Char(t.Transaction_Time, 'yyyy-mm-dd') = a.transaction_day And Trim(t.Transaction_Type) = '130') as financialOptimization,( Select -Sum(Fund) From Stock_Fund_Flow t Where To_Char(t.Transaction_Time, 'yyyy-mm-dd') = a.transaction_day And Trim(t.Transaction_Type) = '10' ) as bond , a.transaction_day from ( select To_Char(t.Transaction_Time, 'yyyy-mm-dd') as transaction_day from Stock_Fund_Flow t
To_Char(t.Transaction_Time, 'yyyy-mm-dd') = #{day}
group by To_Char(t.Transaction_Time, 'yyyy-mm-dd') order by To_Char(t.Transaction_Time, 'yyyy-mm-dd') desc ) a

     	SELECT COUNT(1)  from  ( select   To_Char(t.Transaction_Time, 'yyyy-mm-dd') as  transaction_day  from Stock_Fund_Flow t  
			 
			      To_Char(t.Transaction_Time, 'yyyy-mm-dd') = #{day}
			 
		  
      group by  To_Char(t.Transaction_Time, 'yyyy-mm-dd')    )  a
